import { Component } from '@angular/core';
import { IonicPage, NavController, NavParams, IonicModule, AlertController } from 'ionic-angular';
import { SarvoProfile } from "../../lib/SarvoProfile";
import { HomePage } from "../home/home"; 
// i18n
import {TranslateService} from "@ngx-translate/core";
import {SarvoProfileModel} from "../../lib/models/SarvoProfileModel";
import {SarvoSession} from "../../lib/SarvoSession";
import {BackendFactory} from "../../lib/factory/BackendFactory";
import {Utility} from "../../components/utility";
import { LoginProcessor } from '../../lib/LoginProcessor';
import { CalendarSynchronizer } from '../../lib/generic/calendarSynchronizer';
import { Loadable } from '../../lib/generic/Loadable';
import { SettingsSingleton } from '../../lib/SettingsSingleton';

/**
 * Generated class for the ProfilePage page.
 *
 * See https://ionicframework.com/docs/components/#navigation for more info on
 * Ionic pages and navigation.
 */


@IonicPage()
@Component({
  selector: 'page-profile',
  templateUrl: 'profile.html',
})
export class ProfilePage extends Loadable {

  private _user : SarvoProfile;
  private _model : SarvoProfileModel;
  private _changePicture: boolean;
  private _session: SarvoSession;
  //private selectedCalendar: number;
  private calendarSync: any;
  private loading:boolean = false;

  /* true, if profile page is called from the signup page */
  private _signup : boolean;
  private _number: string;
  private CALENDAR_ACTIVE:boolean;

  constructor(public navCtrl: NavController,
              public navParams: NavParams,
              public translate: TranslateService,
              private alertCtrl: AlertController) {
    // get signup, if not existent or false, profile is not called from
    // signup screen. if it's called from the signup screen, profile needs
    // to be pushed to the backend
    super();
    this.loading = true;
    this._signup = navParams.get('signup');
    this._model = new SarvoProfileModel();
    this._session = SarvoSession.Instance;
    this._changePicture = false;
    this.calendarSync = CalendarSynchronizer;
    this.CALENDAR_ACTIVE = false; // if false calendar functionality is deactivated - currently PROTOTYPE!

    if (this._signup != null && this._signup) {
      // new user (just created)

      // get number from signup screen
      this._number = navParams.get('number');
      this._user = new SarvoProfile(this._model, true, this._number);
      this.loading = false;

      // STORE THE NUMBER TO NATIVE STORAGE
      let l = {"phonenumber": this._user.phonenumber}
      Utility.nativeStorage.setItem("phoneNumberProfileNative", JSON.stringify(l)).then(() => {
        console.log('Stored Phonenumber native!'); //this.receivedData = true;
      },
        error => console.error('Error storing item', error)
      );


      
    } else {
      // user profile already existing, profile called from home (or other) page)
      this._user = new SarvoProfile(this._model, false, this._number);
      
      this.get("curuser/full/").then((request_response) => {
        console.log("user name: -> " + request_response["name"])
        this._user.id = request_response["id"]
        this._user.model.id = request_response["id"]
        this._user.username = request_response["name"]
        this._user.model.username = request_response["name"]
        //this._user.model.phonenumber = request_response["phonenumber"]
        this._user.profilePictureBase64 = request_response["profilePictureBase64"]
        this._user.model.profilePictureBase64 = request_response["profilePictureBase64"]        
        this._user.connectToCalendar = request_response["connectToCalendar"];
        this._user.model.connectToCalendar = request_response["connectToCalendar"];
        this.loading = false;
      }).catch( (err) => {console.log(JSON.stringify(err));});
  
      // READ THIS FROM NATIVE STORAGE
      Utility.nativeStorage.getItem("phoneNumberProfileNative").then(mappingData => {
        var mapp = JSON.parse(mappingData)
        this._user.phonenumber = mapp["phonenumber"]
        this._user.model.phonenumber = mapp["phonenumber"]
      },
      error => 
      {    
      });
      
      
      if(this.CALENDAR_ACTIVE)
      CalendarSynchronizer.Instance.loadCalendarState(false, null); // Load calendar synchronization information
    }

    // Read list of calendars
    if(this.CALENDAR_ACTIVE)
    CalendarSynchronizer.Instance.onActivatedSynchronization();
  }

  changeProfilePic()
  {
    this._changePicture = !this._changePicture;
  }

  changeProfilePicture(type: number) {
    
    // Set Image Gallery
    Utility.imagePicker.getPhoto(type).then((imageDataRaw) => { 
            
      Utility.imagePicker.compress('data:image/jpeg;base64,' + imageDataRaw).then((imageData: string) => 
      {

      // Set new model
      this._model.profilePictureBase64 =   imageData;

      // Upsert the setting
      this._user.postCurrentUserProperties(["profilePictureBase64"]);

        }, (err) => {});
      });
  }

  ionViewWillEnter() {
    // hide taskbar if it was a login
    if(this._signup != null && this._signup) {
      let tabs = document.querySelectorAll('.tabbar');
      if ( tabs !== null ) {
        Object.keys(tabs).map((key) => {
          tabs[ key ].style.transform = 'translateY(56px)';
        });
      } // end if
    }
  }

  ionViewDidLeave() {
    // show taskbar again after FIRST save after signup
    if(this._signup != null && this._signup) {
      let tabs = document.querySelectorAll('.tabbar');
      if ( tabs !== null ) {
        Object.keys(tabs).map((key) => {
          tabs[ key ].style.transform = 'translateY(0)';
        });
      } // end i
    }
  }

  ionViewDidLoad() {
    console.log('ionViewDidLoad ProfilePage');

    // if not signup -- initially choose an image
    if (this._signup != null && this._signup) {
        // Set new model
        this._model.profilePictureBase64 =   "data:image/png;base64,";
    }
  
  }

  showHome(event) {

    /**
     * CALL FROM SIGNUP 
     */
    if (this._signup != null && this._signup) {
      
      // put profile to backend
      this._user.register()
      .then((res : object) => {
        this._session.storeUser( {
          "username": Utility.converter.hash(this._model.phonenumber),
          "secret": res["secret"]
        });

        // upsert DSGVO upsert
        // upsert username and image 
        this._user.agreedDSGVO = SettingsSingleton.getInstance().optInVersion.toString();
        this._user.postCurrentUserProperties(["profilePictureBase64", "username", "agreedDSGVO"]);

        // re show tabbar
        let elem = <HTMLElement>document.querySelector(".tabbar");
        if (elem != null) {
            console.log("did enter " + elem.style.display);
            elem.style.display = 'flex'; // set back to normal
        }

        LoginProcessor.getInstance().shouldBe = 0;
        this.navCtrl.setRoot(HomePage, {signedIn:true}); // set root to disable the "back" function
      })
      .catch((err : object) => {
        console.log(JSON.stringify(err));
      });
    }
    
    
    /**
     * CALL ON PROFILE UPDATE
     */
    else {
      // if not, we just want to update the profile on the backend
      this._user.updateSubset();
      LoginProcessor.getInstance().shouldBe = 0;
    }


    /**
     * CALL FOR BOTH SIGNUP AND UPDATE
     */
    // set calendar
    try { 
      // run synchronization + upsert
      if(this._model.connectToCalendar)
      {
        if(this.CALENDAR_ACTIVE)
        CalendarSynchronizer.Instance.runCalendarSynchronization(this._user);
      }
      else
      {
        if(this.CALENDAR_ACTIVE)
        CalendarSynchronizer.Instance.onDeactivatedSynchronization(this._user);
      }
    } catch (error) {}
    

    // Proceed to home
    let t = this.navCtrl.parent;
    t.select(0); 
    LoginProcessor.getInstance().shouldBe = 0;
    this.navCtrl.setRoot(HomePage, {signedIn:true});
  }

  connectCalendarStateChange()
  {
    // needs to be stored in a singleton
    // singleton holds all information on this 
    //    List of events and ids
    this._model.connectToCalendar = !this._model.connectToCalendar;

    if (this._model.connectToCalendar) // means it changes from false to true
    {
      if(this.CALENDAR_ACTIVE)
      CalendarSynchronizer.Instance.onActivatedSynchronization();
    }
    
  }

  /**
   * GDPR: Delete all data about this user 
   */
  deleteAccountAndData()
  {
    

// Schliessen der Auswahl
        // start an alert asking to change the date if yes send
        let alert = this.alertCtrl.create({
          title: 'Möchtest du deinen Account wirklich löschen?',
          message: "Wenn du dies bestätigst werden alle deine persönlichen Daten, die über dich erhoben wurden gelöscht. Anschließend schließt die App!",
          buttons: [{
              text: 'Nein', role: 'cancel',
              handler: data => {
                  console.log('Cancel clicked');
                  // Hide expansion 
                  
              }},
              {
                  text: 'Ja Account löschen',
                  handler: data => {


                    let alert2 = this.alertCtrl.create({
                      title: 'Du hast die Accountlöschung bestätigt',
                      message: "Wir finden es sehr schade dass du uns verlässt. Natürlich kommen wir deinem Wunsch aber nach. Wenn du dir sicher bis, bestätige hier bitte nochmals, dass du deinen Account wirklich löschen möchtest.",
                      buttons: [{
                          text: 'Nein', role: 'cancel',
                          handler: data => {
                              console.log('Cancel clicked');
                              // Hide expansion 
                              
                          }},
                          {
                              text: 'Ja Account löschen',
                              handler: data => {
                                this.loading = true;
                                this.get("curuser/deleteAccount/").then((request_response) => {
            
                                  // after setting it fixed synchronize calendar
                                  console.log("Account deletion was ok")
            
                                  let alert = this.alertCtrl.create({
                                    title: 'Dein Account wurde gelöscht! ',
                                    buttons: [
                                        {
                                            text: 'Ok',
                                            handler: () => {
                                              navigator["app"].exitApp();
                                              alert.dismiss(false);
                                                return false;
                                            }
                                        }
                                    ]
                                });
                                this.loading = false;
                                alert.present();
                      
                              }).catch((err) => {
                                console.log(JSON.stringify(err));
                                console.log("Account deletion NOT COMPLETED")
                              });
                                  
                              }
                          }
                      ]
                  });
                  alert2.present();
                      
                  }
              }
          ]
      });
      alert.present();


  }

  
}
